INSERT INTO questions (id, question_text, question_type, explanation, reference, topic, subtopic) VALUES (674, 'Match the following leadership concerns with their corresponding focus, distinguishing between Leading concerns and Managing concerns:\n1. Vision\n2. Ends\n3. People\n4. Transformation\nA. Implementation\nB. Means\nC. Systems\nD. Strategic issues\nSelect the correct matching that aligns Leading Concerns with their equivalent concepts, or their contrasting Managing Concerns:', 'Cohort-05-MCQ', 'The correct match is: 1-D, 2-B, 3-C, 4-A.', 'Mock Test – I, Q13', 'Leadership vs Management', 'Concerns');
INSERT INTO questions (id, question_text, question_type, explanation, reference, topic, subtopic) VALUES (675, 'While transformational leadership traditionally emphasizes vision and inspiration, what does pedagogical/instructional leadership primarily emphasize?', 'Cohort-05-MCQ', 'Pedagogical/Instructional leadership emphasizes establishing clear educational goals, planning the curriculum, and evaluating teachers and teaching.', 'Mock Test – I, Q14', 'Leadership Types', 'Pedagogical vs Transformational');
INSERT INTO questions (id, question_text, question_type, explanation, reference, topic, subtopic) VALUES (676, 'The term "the romance of leadership" is used in the source to characterize:', 'Cohort-05-MCQ', 'It refers to leadership as a simple explanation for complex organizational puzzles.', 'Mock Test – I, Q15', 'Leadership Theories', 'Romance of Leadership');
INSERT INTO questions (id, question_text, question_type, explanation, reference, topic, subtopic) VALUES (677, 'Successful school leaders are often flexible rather than dogmatic in their thinking within a system of core values. Which of the following traits are specifically mentioned as helping them push forward when progress is difficult?', 'Cohort-05-MCQ', 'Persistence, resilience, and optimism are traits that help successful leaders continue despite challenges.', 'Mock Test – I, Q16', 'Successful School Leaders', 'Traits');
INSERT INTO questions (id, question_text, question_type, explanation, reference, topic, subtopic) VALUES (678, 'Consider the "four core sets of leadership practices" identified within the transformational leadership model:\nI. Building vision and setting directions\nII. Understanding and developing people\nIII. Redesigning the organization\nIV. Managing the teaching and learning programme\nWhich of the statements given above correctly represents all the core sets?', 'Cohort-05-MCQ', 'All four (I, II, III, and IV) are core practices of transformational leadership.', 'Mock Test – I, Q17', 'Transformational Leadership', 'Core Practices');
INSERT INTO questions (id, question_text, question_type, explanation, reference, topic, subtopic) VALUES (679, 'Which of the following is explicitly recognized as a significant barrier to the effective implementation of distributed leadership in schools?', 'Cohort-05-MCQ', 'Distributed leadership can be perceived as threatening to those in formal power positions.', 'Mock Test – I, Q18', 'Distributed Leadership', 'Barriers');
