INSERT INTO questions (id, question_text, question_type, explanation, reference, topic, subtopic) VALUES (84, 'Which trade-offs did Neutrogena make to sustain its strategic position?', 'MCQ', 'Neutrogena positioned as a medicinal soap, sacrificing volume, price promotions, and efficiencies.', 'Porter, "What Is Strategy?", HBR 1996', 'What’s Strategy', 'Case Example');
